def identificar_fruta():
    print("Responde las siguientes preguntas para identificar la fruta:")
    tiene_semillas = input("¿Tiene la fruta semillas? (si/no) ").lower()
    color = input("¿De qué color es la fruta? ").lower()
    textura = input("¿Cómo es la textura de la fruta? (rugosa/suave/crunchy/arrugada/firme/peluda/espinosa/escamosa) ").lower()
    sabor = input("¿Cómo es el sabor de la fruta? (dulce/agridulce/amargo/neutro/picante/ácido) ").lower()
    tamaño = input("¿Cual es el tamaño de la fruta? (pequeño (1cm a 7cm), mediano (8 a 15 cm) o grande (16 cm en adelante)) ").lower()

    frutas_coincidentes = []

    for fruta, caracteristicas in frutas_caracteristicas.items():
        if (tiene_semillas == "si" and caracteristicas["semillas"] or tiene_semillas == "no" and not caracteristicas["semillas"]) and \
                caracteristicas["color"] == color and caracteristicas["textura"] == textura and caracteristicas["sabor"] == sabor and caracteristicas["tamaño"] == tamaño:
            respuesta = input(f"¿Es {fruta} la fruta que estás pensando? (si/no) ").lower()
            if respuesta == "si":
                return fruta, caracteristicas["originaria"]
        elif (tiene_semillas == "si" and caracteristicas["semillas"] or tiene_semillas == "no" and not caracteristicas["semillas"]) and \
                caracteristicas["color"] == color and caracteristicas["textura"] == textura and caracteristicas["sabor"] == sabor:
            frutas_coincidentes.append(fruta)

    if frutas_coincidentes:
        print("No se pudo identificar la fruta.")
        print("Frutas que coinciden con las características proporcionadas:")
        for fruta in frutas_coincidentes:
            print(fruta)
    else:
        print("No se pudo identificar la fruta.")

    return None, None

# Llamada a la función
fruta_identificada, origen = identificar_fruta()
if fruta_identificada:
    print("La fruta identificada es:", fruta_identificada)
    print("Origen de la fruta:", origen)
